public class ControllaStringa {
    public static void main(String[] args) {
        boolean iter = tutteCifreIter("");
        boolean recur  = (tutteCifreRecur("", 0)); // aggiungere wrap per utilizzare il metodo senza l'inserimento del valore di partenza 0
        System.out.println(iter);
        System.out.println(recur);
    }

    static boolean tutteCifreIter(String word){
        boolean result = false;
        for (int i = 0; i < word.length(); i++) {
            if(word.charAt(i) >= '0' && word.charAt(i) <= '9'){ // viene controllato il valore numerico del carattere
                result = true;
            }else{
                result = false;
                break;
            }
        }    
        return result;
    }

    static boolean tutteCifreRecur(String word, int CharToCheck){
        boolean result = true;
        if(word.charAt(CharToCheck)>='0' && word.charAt(CharToCheck)<='9'){
            if(CharToCheck < word.length()){
                if(CharToCheck+1 < word.length())
                result = tutteCifreRecur(word, CharToCheck+1);
            }
        }else{
            result = false;
        }
        return result;
    }
}
